      "case_study_id": null,
      "lab_id": 0,
      "question_text": "<p>Which two are true about diagnosing Oracle Database failure situations using Data Recovery Advisor? (Choose two.)<br></p>",
      "mark": 1,
      "is_partially_correct": true,
      "question_type": "1",
      "difficulty_level": "0",
      "general_feedback": "<p>Correct Answer: DE</p>",
      "is_active": true,
